
restraint_strings = ['x', 'y', 'z', 'XX', 'YY', 'ZZ']


def parse_res(res):
    """

    :param res:
    :return:
    """
    res_bitmask = 0
    index = 0

    for i, r in enumerate(restraint_strings):
        if res.startswith(r, index):
            res_bitmask = res_bitmask | (1 << i)
            index = index + len(r)

    return res_bitmask


def print_res(res):
    """

    :param res:
    :return:
    """
    res_string = ""
    for i, r in enumerate(restraint_strings):
        if res & (1 << i):
            res_string = res_string + r

    return res_string
