from .parse_list import *


class GSAError(Exception):
    """
    A class for passing GSA - specific error messages.
    """
    def __init__(self, *args, **kwargs):
        Exception.__init__(self, *args, **kwargs)


def strip_quotes(s: str) -> str:
    '''
    Removes a leading and trailing " if they exist

    :param s: quoted string
    :return: string with quotes removed or original string if it has no quotes
    '''
    if s.startswith('"') and s.endswith('"'):
        return s[1:-1]
    return s